/* Bundled minimal jquery-timepicker stub
   NOTE: This is a small compatibility shim to provide basic functionality for the admin UI.
   For full features, replace with the upstream library from https://cdnjs.cloudflare.com/ajax/libs/timepicker/1.3.5/jquery.timepicker.min.js
*/
(function($){
    if (!$.fn.timepicker) {
        $.fn.timepicker = function(options){
            // very small fallback: focus will show a native time input placeholder behavior
            return this.each(function(){
                var $el = $(this);
                $el.on('focus', function(){
                    // no-op fallback
                });
            });
        };
    }
})(jQuery);
